import { createSlice } from '@reduxjs/toolkit'

let newid = 0;

const createItem = (lable) => {
    return{
      lable,
      important: false,
      done: false,
      id: newid++,
    };
    };

export const todoitems = createSlice({
  name: 'todoitems',
  initialState: {
    todoData: [
        createItem("First"),
        createItem("2"),
        createItem("3")
      ],
      term: "",
      filter: "active"
  },
  reducers: {
    addTodoItem: (state,lable) => {

        const newItem = createItem(lable.payload);



        let newArray = [...state.todoData, newItem]; 

        state.todoData = newArray;

    },
    DelTodoItem: (state,e) => {

      const id = e.payload;
      const idx = state.todoData.findIndex(el=> el.id == id);

      let newArray = [...state.todoData.slice(0, idx),...state.todoData.slice(idx+1)];

      state.todoData = newArray;
      },
      ImportantTodoItem: (state,e) => {

        const id = e.payload;
        const idx = state.todoData.findIndex(el=> el.id == id);
        const arr = state.todoData;
        const oldItem = arr[idx];
        console.log(id,idx)
        const prop = "important"
        const newItem = {...oldItem, [prop]: !oldItem[prop]};


        let newArray = [...state.todoData.slice(0, idx),newItem,...state.todoData.slice(idx+1)];

        state.todoData = newArray;
        },
    },
})

// Action creators are generated for each case reducer function
export const { addTodoItem, DelTodoItem,ImportantTodoItem } = todoitems.actions

export default todoitems.reducer
